import CardInfo from "./CardInfo";
import info from "./info";

class Card extends Component{
  constructor(){
    super()
    this.state = { info: info
}
  }
  mapping(x){
    const members = x.map(member=> <CardInfo
      key={member.id}
      img = {member.img}
      name={member.name}
      position={member.position}
      pohne={member.pohne}
      email={member.email} 
    />);
return members
  }
  
 
                  
